import { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  const [enteredTitle, SetEnteredTitle] = useState("");
  const [enteredAmount, SetEnteredAmount] = useState("");
  const [enteredDate, SetEnteredDate] = useState("");
  const TitleChangeHandler = (e) => {
    const title = document.getElementById("titleinput").value;
    SetEnteredTitle(title);
  };
  const AmountChangeHandler = (e) => {
    const amount = document.getElementById("AmountInput").value;
    SetEnteredAmount(amount);
    console.log(amount)
  };
  const DateChangeHandler = (e) => {
    const date = document.getElementById("DateInput").value;
    SetEnteredDate(date);
  };
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" id="titleinput" onChange={TitleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="text" min="0.01" step="0.01" id="AmountInput" onChange={AmountChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2023-01-01" id="DateInput" onChange={DateChangeHandler} />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};
export default ExpenseForm;
