import "./Expenses.css";
import ExpenseItem from "./ExpenseItems";
import Card from "../UI/card";
import ExpenseFilter from "./ExpenseFilter";
import { useState } from "react";
const Expenses = (props) => {
  const [FilterYear, SetFilterYear] = useState("2020");

  const FilterChagneHandler = (year) => {
    SetFilterYear(year);
  };
  const filteredExpenses= props.items.filter((expenses)=>{
    return expenses.date.getFullYear().toString()===FilterYear
  })
  return (
    <Card className="expenses">
      <ExpenseFilter select={FilterYear} selectedyear={FilterChagneHandler} />
      {filteredExpenses.length===0 ? <p>There is not content</p>:filteredExpenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          price={expense.amount}
          date={expense.date}
        />
      ))}
      {}
    </Card>
  );
};
export default Expenses;
