import "./Expenses.css";
import ExpenseItem from "./ExpenseItems";
import Card from "../UI/card";
import ExpenseFilter from "./ExpenseFilter";
import { useState } from "react";
const Expenses=(props)=> {
  const [FilterYear,SetFilterYear]=useState('2020')
  let filtertext=" 2019, 2020 and 2021"
   if(FilterYear==="2019"){
    filtertext="2020 and 2021"
   }else if(FilterYear==="2020"){
    filtertext="2019 and 2021"
   }
  const FilterChagneHandler=(year)=>{
    SetFilterYear(year)
  }
  return (
    <Card className="expenses" >
      <ExpenseFilter  selectedyear={FilterChagneHandler}/>
      <p>Select years are{filtertext}</p>
      <ExpenseItem
        title={props.items[0].title}
        price={props.items[0].amount}
        date={props.items[0].date}
      />
      <ExpenseItem
        title={props.items[1].title}
        price={props.items[1].amount}
        date={props.items[1].date}
      />
      <ExpenseItem
        title={props.items[2].title}
        price={props.items[2].amount}
        date={props.items[2].date}
      />
      <ExpenseItem
        title={props.items[3].title}
        price={props.items[3].amount}
        date={props.items[3].date}
      />
    </Card>
  );
}
export default Expenses;
